import React, { useState } from 'react'
import Image from '../../img/bookcase-books-bookshelves-270571 (1).jpg';
import './Home.css';
import { MockData } from '../MockData/HomeMock';
const Home = () => {
  const [Filter, SetFilter] = useState(MockData);
  const [sdata,setSdata]=useState();
  let Data = Filter.map((data) => {
    return (
      <div className='container d-flex ' id='inner'>
        <div className="card w-100 mt-5 col-md-3" >
          <img src={data.Img} className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">{data.Title}</h5>
            <p className='card-text'>{data.Author}</p>
            <p className="card-text">{data.Description}</p>
            <a className="btn btn-secondary d-flex justify-content-center"  >Show Path</a>
          </div>
        </div>
      </div>
    )
  })
  // console.log(MockData);

  //Filtering function
  function Filtering(data) {
    const list = MockData.filter((x) => x.category === data);
    console.log(data);
    SetFilter(list);
    console.log(list);
  }
