  e.preventDefault();
  var data=document.getElementById('input1').value;
  console.log(data);
  const list1=MockData.filter((fdata) => fdata.category===data
    // const d=fdata.category.toLowerCase();
    // console.log(d);
    // if(d.search(d))
    // {
    //   console.log(d.search(data));
    // }
  )
  let trdata=[];
  MockData.map((e)=>{
   let dd=e.Title;
  const trial=dd.search(data);
  // console.log(trial);
  if(!trial){
    trdata=[...trdata,e];
  }
  })
  console.log(trdata);
  setSdata(trdata);
  
}
