})
// console.log(sdata);

  return (
    <div>
      <nav class="navbar navbar-expand-lg navbar-dark  bg-dark pe-1">
        <div class="container">
          <a class="navbar-brand">SEARCH_LESS</a>
          <form class="d-flex">
            <input class="form-control me-2" type="search_Less" placeholder="Search" aria-label="Search" id='input1' />
            <button class="btn btn-outline-light" type="submit" onClick={searchop} >Search</button>
          </form>
        </div>
      </nav>
      <div className='container d-flex justify-content-center mt-3' id="Cards">
        <button className='btn btn-outline-dark mx-2' onClick={() => SetFilter(MockData)}>All</button>
        <button className='btn btn-outline-dark mx-2' onClick={() => Filtering("Programming")}>Programming</button>
        <button className='btn btn-outline-dark mx-2' onClick={() => Filtering("Course")}>Course</button>
        <button className='btn btn-outline-dark mx-2' onClick={() => Filtering("DSA")}>DSA</button>
        <button className='btn btn-outline-dark mx-2' onClick={() => Filtering("Chemical")}>Chemical</button>
        <button className='btn btn-outline-dark mx-2' onClick={() => Filtering("Physics")}>Physics</button>
        <button className='btn btn-outline-dark mx-2' onClick={() => Filtering("Maths")}>Maths</button>
      </div>
      <div className='container d flex' id="Outer-Home">
        {Data}
      </div>
    </div>

  )
}

export default Home
